'use strict';

const crypto = require('crypto');

/**
 * Verify bot is legal or not
 **/
class Certificate {
    /**
     * @param {String} private key content,if you don't use bot-sdk,
     * you should generated private key by follow document.
     **/
    constructor (privateKeyContent = '') {
        this._privateKey = privateKeyContent;
    }


    /**
     * @param {String} the content need to sign
     * @return {bool} is sign succeeded.
     **/
    getSign(content) {
        try {
            var sign = crypto.createSign('RSA-SHA1');
            sign.update(content);
            sign = sign.sign(this._privateKey, 'base64');
            return sign;
        } catch(err) {
            return false;
        }
    }
}
 
module.exports = Certificate;
