/**
 * @file Config class,contains configuration information about data upload,
 * Under normal circumstances, you do not need to modify the configuration
 * items inside.
 * @author liudesheng01<liudesheng01@baidu.com>
 */

'use strict';

/**
 * Configuration information,contains the data reported configuration add sdk info
 **/
class Config {
    constructor() {
        this._uploadUrl = 'dueros-api.baidu.com';
        this._sdkVersion = '1.0.0';
        this._sdkType = 'nodejs';
        this._uploadPort = 443;
        this._uploadPath = '/uploadmonitordata';
    }

    /**
     * get server upload url
     *
     * @return {string}
     **/
    getUploadurl() {
        return this._uploadUrl;
    }

    /**
     * get sdk version
     *
     * @return {string}
     **/
    getSdkVersion() {
        return this._sdkVersion;
    }

    /**
     * get sdk type
     *
     * @return {string}
     **/
    getSdkType() {
        return this._sdkType;
    }

    /**
     * get upload port
     *
     * @return {string}
     **/
    getUploadPort() {
        return this._uploadPort;
    }

    /**
     * get upload path
     *
     * @return {string}
     **/
    getUploadPath() {
        return this._uploadPath;
    }
}

module.exports = Config;