'use strict';

/**
 * According to the bot protocol, get the required data from response
 **/
class Response {
    
    /**
     * @param {Requset} @param {Object} data response body
     **/
    constructor (data) {
        if (!data) {
            throw new Error('data is undefined');
        }
        this._data = data;
    }


    /**
     * @returns {string} get current slot name
     */
    getSlotName() {
        if (this._data.response && this._data.response.directives
            && this._data.response.directives.length > 0) {
            return this._data.response.directives[0].slotToElicit;
        }
        return '';
    }

    /**
     * @returns {string} Whether the current session is over
     */
    getShouldEndSession() {
        if (this._data.response) {
            return this._data.response.shouldEndSession;
        }
    }

    /**
     * @returns {string} get bot output speech
     */
    getOutputSpeech() {
        if (this._data.response) {
            return this._data.response.outputSpeech;
        }
    }

    /**
     * @returns {string} get prompt content
     */
    getReprompt() {
        if (this._data.response && this._data.response.reprompt) {
            return this._data.response.reprompt.outputSpeech;
        }
    }
}

module.exports = Response;
