require("should");

let assert = require('assert');

let Response = require('../lib/Response');
let responseData = require('../data/intent_response_tax');

describe('Response Test', () => {
    let response = new Response(responseData);

    describe('#getSlotName()', function() {
        it('get slot name must be monthlysalary', function() {
            response.getSlotName().should.equal("monthlysalary");
        });
    });

    describe('#getShouldEndSession()', function() {
        it('getShouldEndSession must be false', function() {
            response.getShouldEndSession().should.be.false();
        });
    });

    describe('#getOutputSpeech()', function() {
        var outputspeech = { type: 'SSML', text: '', ssml: 'java-sdk您的税前工资是多少呢?' };
        it('getOutputSpeech must be outputspeech', function() {
            response.getOutputSpeech().should.deepEqual(outputspeech);
        });
    });

    describe('#getReprompt()', function() {
        var repromt = { type: 'SSML', text: '', ssml: 'java-sdk您的税前工资是多少呢?' };
        it('getReprompt must be false', function() {
            response.getReprompt().should.deepEqual(repromt);
        });
    });

});
